    }, {
      text: "No",
      value: false
    }]
  };
  //check box
  checked = false;
  indeterminate = false;
  labelPosition = 'after';
  disabled = false;
  dor = new FormControl(new Date());
  // Define a custom date format
  customDateFormat = 'yyyy-MM-dd';
  EmpId;
  EmpExperienceInfo;
  addingMode = true;
  // Inside your component class
  isCurrentlyWorking = false;
  fileUploadForm;
  EmployeeDocuments = [];
  typeValue;
  images = [];
  docFunctionList = [];
  dataname;
  filenames;
  fileextension;
  file_ext;
