  autocompletetedevScroll() {
    setTimeout(() => {
      if (this.matdevAutocomplete && this.autocompleteTrigger && this.matdevAutocomplete.panel) {
        fromEvent(this.matdevAutocomplete.panel.nativeElement, 'scroll').pipe(map(x => this.matdevAutocomplete.panel.nativeElement.scrollTop), takeUntil(this.autocompleteTrigger.panelClosingActions)).subscribe(x => {
          const scrollTop = this.matdevAutocomplete.panel.nativeElement.scrollTop;
          const scrollHeight = this.matdevAutocomplete.panel.nativeElement.scrollHeight;
          const elementHeight = this.matdevAutocomplete.panel.nativeElement.clientHeight;
          const atBottom = scrollHeight - 1 <= scrollTop + elementHeight;
          if (atBottom) {
            if (this.has_next === true) {
              this.taskreportservice.getdeveloperFilter(this.developerInput.nativeElement.value, this.currentpage + 1).subscribe(results => {
                let datas = results["data"];
                let datapagination = results["pagination"];
                this.developerList = this.developerList.concat(datas);
                if (this.developerList.length >= 0) {
                  this.has_next = datapagination.has_next;
                  this.has_previous = datapagination.has_previous;
                  this.currentpage = datapagination.index;
                }
              });
            }
          }
        });
      }
    });
  }
