      // task_status: data.task_status_id,
      emp_hr: data?.employee_hours,
      delay_days: data.DynamicDelayDaysCount,
      reason_for_delay: data.reason_for_delay
    }
    console.log(data, obj)
    this.TaskManagerService.ChaneStatus(obj, data.id)
      .subscribe(results => {
        if(results.code){
