-input[_ngcontent-%COMP%] {\n  background-color: aliceblue;\n}\n\n.btn-outline-info[_ngcontent-%COMP%] {\n  margin-left: 51%;\n  font-size: small;\n}\n\n.delete[_ngcontent-%COMP%], .edit[_ngcontent-%COMP%] {\n  transition: opacity 0.5s;\n}\n\n.delete[_ngcontent-%COMP%]:hover {\n  opacity: 0.7;\n}\n\n.edit[_ngcontent-%COMP%]:hover {\n  opacity: 0.7;\n}\n\n.fa-pencil[_ngcontent-%COMP%] {\n  background-color: #ededff;\n  color: #474aff;\n  padding: 10px;\n  border-radius: 4px;\n}\n\n.fa-trash[_ngcontent-%COMP%] {\n  border-radius: 4px;\n  background-color: #fff0d5;\n  padding: 10px;\n  color: #ffa500;\n}\n\n.fa-trash-can[_ngcontent-%COMP%] {\n  border-radius: 4px;\n  background-color: #fff0d5;\n  padding: 10px;\n  color: #ffa500;\n}\n\n.uploadIcon[_ngcontent-%COMP%] {\n  display: inline-block;\n  margin-left: 28px;\n}\n\n.DownloadButton[_ngcontent-%COMP%] {\n  margin-left: 20px;\n  display: inline-block;\n}\n\n.AdditionalDeductionNav[_ngcontent-%COMP%] {\n  display: flex;\n  align-items: center;\n}\n\n.SearchButton[_ngcontent-%COMP%] {\n  margin-left: 20px;\n  background-color: white;\n}\n\n.ClearButton[_ngcontent-%COMP%] {\n  margin-left: 20px;\n  background-color: white;\n}\n\n.AddButton[_ngcontent-%COMP%] {\n  margin-left: 17px;\n  background-color: rgba(45, 129, 155, 0.56);\n  margin-top: 10px;\n}\n\n@media (max-width: 1450px) {\n  .uploadIcon[_ngcontent-%COMP%] {\n    margin-left: 5px;\n  }\n}\n@media (max-width: 1340px) {\n  .uploadIcon[_ngcontent-%COMP%] {\n    margin-left: 2px;\n  }\n}\n@media (max-width: 1330px) {\n  .uploadIcon[_ngcontent-%COMP%] {\n    margin-left: 0px;\n  }\n}\n@media (max-width: 1320px) {\n  .SearchButton[_ngcontent-%COMP%] {\n    margin-left: 10px;\n  }\n  .ClearButton[_ngcontent-%COMP%] {\n    margin-left: 10px;\n  }\n  .AddButton[_ngcontent-%COMP%] {\n    margin-left: 7px;\n  }\n  .uploadIcon[_ngcontent-%COMP%] {\n    margin-left: 2px;\n  }\n}\n/*# sourceMappingURL=data:application/json;charset=utf-8;base64, */"]
