      if (obj[i] == null || obj[i] == "" || obj[i] == undefined) {
        obj[i] = '';
      }
    }
    this.SpinnerService.show();
    if (hint == 'next') {
      this.serviceCallProjectSummary(obj, this.presentpageProject + 1, 10);
    } else if (hint == 'previous') {
      this.serviceCallProjectSummary(obj, this.presentpageProject - 1, 10);
    } else {
      this.serviceCallProjectSummary(obj, 1, 10);
    }
  }
