  employeeautocomplete() {
    setTimeout(() => {
      if (this.matcommodityAutocomplete && this.autocompletetrigger && this.matcommodityAutocomplete.panel) {
        fromEvent(this.matcommodityAutocomplete.panel.nativeElement, 'scroll').pipe(map(x => this.matcommodityAutocomplete.panel.nativeElement.scrollTop), takeUntil(this.autocompletetrigger.panelClosingActions)).subscribe(x => {
          const scrollTop = this.matcommodityAutocomplete.panel.nativeElement.scrollTop;
          const scrollHeight = this.matcommodityAutocomplete.panel.nativeElement.scrollHeight;
          const elementHeight = this.matcommodityAutocomplete.panel.nativeElement.clientHeight;
          const atBottom = scrollHeight - 1 <= scrollTop + elementHeight;
          if (atBottom) {
            if (this.has_nextcom === true) {
              this.taskmanagerservice.task_employeesearch(this.commodityInput.nativeElement.value, this.currentpagecom + 1).subscribe(results => {
                let datas = results["data"];
                let datapagination = results["pagination"];
                this.empList = this.empList.concat(datas);
                if (this.empList.length >= 0) {
                  this.has_nextcom = datapagination.has_next;
                  this.has_previouscom = datapagination.has_previous;
                  this.currentpagecom = datapagination.index;
                }
              }, error => {
                this.errorHandler.handleError(error);
                this.SpinnerService.hide();
              });
            }
          }
        });
      }
    });
  }
