    }
  }
  removeValueFromArray(removeId: number): void {
    const index = this.draftsList.indexOf(removeId);
    if (index !== -1) {
      this.draftsList.splice(index, 1);
    } else {
    }
  }
  onMasterCheckboxClick(event: any) {
