        for (const role of entry.role) {
          if (role.name === 'Payroll Admin') {
            hasAdmin = true;
          } else if (role.name === 'Maker') {
            hasMaker = true;
          } else if (role.name === 'Payroll Checker') {
            hasChecker = true;
          }
        }
      }
