    EmpTaskSummary: true,
    EmpTaskCreate: false,
    EmpHoursHistory: false,
    ApprovalStatusList: [],
    pagesize: 10,
    taskLists: [],
    presentpageTask: 1,
    hasPreviousTask: false,
    hasNextTask: false,
    devtypelist: [],
    clientList: [],
    appNameList: [],
    statusList: [],
    moduleNameList: [],
    teamldList: [],
    DayTaskData: [],
    taskHourList: [],
