  getEmpBankDetails() {
    this.spinner.show();
    this.hrmsService.getEmpBankDetailsNew(this.EmpId).subscribe(results => {
      if (results.code) {
        this.spinner.hide();
        this.toastr.error(results.code);
      } else {
        this.spinner.hide();
        this.EmpBankDetails = results['bank'];
      }
    });
  }
  keyPressNumbers(event) {
    var charCode = event.which ? event.which : event.keyCode;
    if (charCode < 48 || charCode > 57) {
      event.preventDefault();
      return false;
    } else {
      return true;
    }
  }
