croll;\n  border: 1px solid rgba(0, 0, 0, 0.082);\n}\n\n.bordercomments[_ngcontent-%COMP%]::-webkit-scrollbar {\n  width: 4px;\n}\n\n\n\n.bordercomments[_ngcontent-%COMP%]::-webkit-scrollbar-track {\n  background: #e4e3e3;\n}\n\n.bordercomments[_ngcontent-%COMP%]::-webkit-scrollbar-thumb:hover {\n  background: #555;\n}\n\n.jumbotronView[_ngcontent-%COMP%] {\n  min-width: 500px;\n  max-width: 900px;\n  max-height: 500px;\n  margin: 5px;\n  padding: 5px;\n  background-color: #e9ecef;\n  overflow: hidden;\n  overflow: scroll;\n}\n\n.navbar-light[_ngcontent-%COMP%]   .navbar-nav[_ngcontent-%COMP%]   .nav-link[_ngcontent-%COMP%] {\n  color: black;\n  font-size: 18px;\n  cursor: pointer;\n}\n\n.tab-btn[_ngcontent-%COMP%] {\n  margin-left: 81%;\n  position: relative;\n}\n\n#navshow[_ngcontent-%COMP%] {\n  width: 75%;\n  margin-left: 10px;\n}\n\n.nav-item.show[_ngcontent-%COMP%]   .nav-link[_ngcontent-%COMP%], .nav-tabs[_ngcontent-%COMP%]   .nav-link.active[_ngcontent-%COMP%] {\n  background-color: transparent !important;\n  color: #044063 !important;\n  text-decoration: underline;\n  cursor: pointer;\n}\n\n.nav-tabs[_ngcontent-%COMP%]   .nav-link[_ngcontent-%COMP%]:hover {\n  border: none !important;\n  cursor: pointer;\n}\n\n.nav-tabs[_ngcontent-%COMP%]   .nav-link[_ngcontent-%COMP%] {\n  border: none;\n  cursor: pointer;\n}\n\n.paddingsizediv[_ngcontent-%COMP%] {\n  padding: 0px 10px 10px 0px;\n}\n\n.checkboxsize[_ngcontent-%COMP%] {\n  width: 200px;\n}\n\n.ulminmax[_ngcontent-%COMP%] {\n  padding: 0px;\n}\n\n.minmaxheader[_ngcontent-%COMP%] {\n  border: 1px solid rgba(0, 0, 0, 0.087);\n  list-style-type: none;\n  padding: 10px;\n  text-align: center;\n}\n\n.minmax[_ngcontent-%COMP%] {\n  min-width: 200px;\n  max-width: 600px;\n  height: 100px;\n  border: 1px solid rgba(0, 0, 0, 0.087);\n  list-style-type: none;\n  padding: 10px;\n  text-align: center;\n}\n\n.btncolor[_ngcontent-%COMP%] {\n  background-color: #e0e0e0;\n}\n\n.list[_ngcontent-%COMP%]   td[_ngcontent-%COMP%]:nth-child(1) {\n  \n\n  padding-left: 20px;\n}\n\n.list[_ngcontent-%COMP%]   td[_ngcontent-%COMP%]:nth-child(2) {\n  \n\n  padding-left: 20px;\n}\n\n.tableForEvaluator[_ngcontent-%COMP%]   th[_ngcontent-%COMP%], .tableForEvaluator[_ngcontent-%COMP%]   td[_ngcontent-%COMP%] {\n  font-size: 12px;\n  padding: 10px;\n  white-space: pre;\n}\n/*# sourceMappingURL=data:application/json;charset=utf-8;base64, */"]
