issuefunction(){
  let date=this.IssueSummaryForm.value.dynamicdropdown
  if(date==1){
    this.startdate=true
    this.enddate=false
    this.query=false
    this.app_id =false
    this.client =false
    this.module_id  =false
    this.priority   =false
    this.statusss   =false
  }
  else if(date==2){
    this.startdate=false
    this.enddate=true
    this.query=false
    this.app_id =false
    this.client =false
    this.module_id  =false
    this.priority   =false
    this.statusss   =false
  }
  else if(date==3){
    this.startdate=false
    this.enddate=false
    this.query=true
    this.app_id =false
    this.client =false
    this.module_id  =false
    this.priority   =false
    this.statusss   =false
  }
  else if(date==4){
    this.startdate=false
    this.enddate=false
    this.query=false
    this.app_id =true
    this.client =false
    this.module_id  =false
    this.priority   =false
    this.statusss   =false
  }
  else if(date==5){
    this.startdate=false
    this.enddate=false
    this.query=false
    this.app_id =false
    this.client =true
    this.module_id  =false
    this.priority   =false
    this.statusss   =false
  }
  else if(date==6){
    this.startdate=false
    this.enddate=false
    this.query=false
    this.app_id =false
    this.client =false
    this.module_id  =true
    this.priority   =false
    this.statusss   =false
  }
  else if(date==7){
    this.startdate=false
    this.enddate=false
    this.query=false
    this.app_id =false
    this.client =false
    this.module_id  =false
    this.priority   =true
    this.statusss   =false
  }
  else if(date==8){
    this.startdate=false
    this.enddate=false
    this.query=false
    this.app_id =false
    this.client =false
    this.module_id  =false
    this.priority   =false
    this.statusss   =true
  }
this.inpstartdatesprint="Start date :"+this.taskSummaryForm.value.start_date
this.inpenddatesprint="End date :"+this.taskSummaryForm.value.end_date
console.log("inpstartdatesprint",this.inpstartdatesprint)
console.log("inpenddatesprint",this.inpenddatesprint)
}





getAppdrop() {
  this.TaskManagerService.getApp('').subscribe(res=>{
    this.appdrop = res['data']
  })
}
getClientdrop() {
  this.TaskManagerService.getClient('').subscribe(res=>{
    this.clientdrop = res['data']
  })
}
getModuledrop() {
  this.TaskManagerService.getmodule('').subscribe(res=>{
    this.moduledrop = res['data']
  })
}
getPrioritydrop() {
  this.TaskManagerService.getpriority().subscribe(res=>{
    this.prioritydrop = res
  })
}
issuestatusdrop() {
  this.TaskManagerService.getissues().subscribe(res=>{
    this.ststusdrop= res['data']
  })
}
statusdrop() {
  this.TaskManagerService.gesta('').subscribe(res=>{
    this.statusdrops= res
  })
}
viewIssue(data)
  {
    this.localservice.taskid.next(data.id)
   this.isShowIssues = false;
   this.taskview = true;
  }
  appClick() {
    let devkeyvalue: String = "";
    // this.getsprint(devkeyvalue);
    // (this.taskAddForm.get('data') as FormArray).at(i).get('developer').valueChanges
    this.IssueSummaryForm.get('app_id').valueChanges
      .pipe(
        debounceTime(100),
        distinctUntilChanged(),
        tap(() => {
          this.isLoading = true;
          console.log('inside tap')

        }),
        switchMap(value => this.TaskManagerService.getApp(value)
          .pipe(
            finalize(() => {
              this.isLoading = false
            }),
          )
        )
      )
      .subscribe((results: any[]) => {
        let datas = results["data"];
        this.appdrop = datas;




      })

  

  
}

backtoTaskSummary()
{
  this.taskview = false;
  this.isShowIssues= true;
  this.issueSearch('')
  this.pageNumbers = 1;
}
resetTasks()
{
  this.IssueSummaryForm.reset();
  this.issueSearch('')

}
deleteIssue(data)
{
  this.TaskManagerService.deleteIssue(data.id).subscribe(res => {
    if(res.code)
    {
      this.notification.showError(res.code)
    }
    else
    {  
        this.notification.showSuccess(res.message);
        this.issueSearch('')
    }
  })
}

}
<div *ngIf="isShowIssues">
  <div>
